import {Component} from 'react';
import React from 'react';
import './MyForm.css';
class MyForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: '',
      lastName: '',
      email: '',
      dob: null,
    };
  }
  myChangeHandler = (event) => {
    let nam = event.target.name;
    let val = event.target.value;
    this.setState({
      [nam]: val
    })
  }
  render() {
    return (
      <form class="form-inline">
        <p class="elem">First Name: </p> <input class="elem-2" type="text" name="firstName" onChange={this.myChangeHandler}/>
        <p class="elem">Last Name: </p> <input class="elem-2" type="text" name="lastName" onChange={this.myChangeHandler}/>
        <p class="elem">Email: </p> <input class="elem-2" type="text" name="email" onChange={this.myChangeHandler}/>
        <p class="elem">DOB: </p> <input class="elem-2" type="text" name="dob" onChange={this.myChangeHandler}/>
      </form>
    );
  }
}
export default MyForm;
