class MyForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: '',
      lastName: '',
      email: '',
      dob: null,
    };
  }
  myChangeHandler = (event) => {
    let nam = event.target.name;
    let val = event.target.value;
    this.setState({
      [nam]: val
    })
  }
  render() {
    return (
      <form>
        <p>First Name: </p> <input type="text" name="firstName" onChange={this.myChangeHandler}/>
        <p>Last Name: </p> <input type="text" name="lastName" onChange={this.myChangeHandler}/>
        <p>Email: </p> <input type="text" name="email" onChange={this.myChangeHandler}/>
        <p>DOB: </p> <input type="text" name="dob" onChange={this.myChangeHandler}/>
      </form>
    );
  }
}

ReactDOM.render(<MyForm />, document.getElementById('root'));
